def parse_brackets_basement_position(bracket_string):
    floors, basement_position = 0, 0
    for i in range(len(bracket_string)):
        if bracket_string[i] == "(":
            floors += 1
        elif bracket_string[i] == ")":
            floors -= 1
        if floors == -1:
            basement_position = i + 1
            break
    return basement_position

